#include <iostream>#include <vector>#include <iomanip>#include <cmath>#include <algorithm>using namespace std;int main() {	int n;	cin >> n;	vector<int> dp(n + 1, 1000000);	for (int i = 1; i <= sqrt(n); i++) {		dp[i * i] = 1;	}	for (int i = 2; i <= n; i++) {		for(int j = 1; j * j < i; j++) {			dp[i] = min(dp[i], 1 + dp[i - j * j]);		}	}	cout << dp[n];	return 0;}